--
-- Структура для представления `attractioninfo`
--
DROP TABLE IF EXISTS `attractioninfo`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`127.0.0.1` SQL SECURITY DEFINER VIEW `attractioninfo`  AS  select `attraction`.`id` AS `id`,`attraction`.`name` AS `name`,`type`.`name` AS `type`,`categories`.`name` AS `category`,`attraction`.`address` AS `address`,`locality`.`name` AS `locality`,`attraction`.`coordinates` AS `coordinates`,`attraction`.`discription` AS `discription`,`attraction`.`is_suggest` AS `is_suggest` from (((`attraction` join `categories` on((`attraction`.`category_id` = `categories`.`id`))) join `locality` on((`attraction`.`locality_id` = `locality`.`id`))) join `type` on((`attraction`.`types_id` = `type`.`id`))) ;

-- --------------------------------------------------------

--
-- Структура для представления `newsinfo`
--
DROP TABLE IF EXISTS `newsinfo`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`127.0.0.1` SQL SECURITY DEFINER VIEW `newsinfo`  AS  select `news`.`id` AS `id`,`news`.`name` AS `name`,`news`.`discription` AS `discription`,`news`.`date` AS `date`,`news`.`is_suggest` AS `is_suggest`,`usersdost`.`email` AS `user` from (`news` join `usersdost` on((`news`.`user_id` = `usersdost`.`id`))) ;

-- --------------------------------------------------------

--
-- Структура для представления `ratinginfo`
--
DROP TABLE IF EXISTS `ratinginfo`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`127.0.0.1` SQL SECURITY DEFINER VIEW `ratinginfo`  AS  select `attraction`.`name` AS `attraction_name`,`usersdost`.`email` AS `user`,`rating`.`rate` AS `rate` from ((`rating` join `attraction` on((`rating`.`id_attraction` = `attraction`.`id`))) join `usersdost` on((`rating`.`id_user` = `usersdost`.`id`))) ;

